import { legacy_createStore } from "redux";
var counter = 0;
const counterReducer = (state = counter, action) => {
  if (action.type === "INCREMENT") {
    return {
      counter: state + 1,
    };
  }
  if (action.type === "DECREMENT") {
    return {
      counter: state - 1,
    };
  }
  return state;
};
const store = legacy_createStore(counterReducer);
export default store;
