import "./Header.scss";
import { useSelector } from "react-redux";

const Header = () => {
  const isauthorised = useSelector(
    (state) => state.authentication.isauthorised
  );
  return (
    <header className="header">
      <h1>Redux Auth</h1>
      {isauthorised && (
        <nav>
          <ul>
            <li>
              <a href="/">My Products</a>
            </li>
            <li>
              <a href="/">My Sales</a>
            </li>
            <li>
              <button>Logout</button>
            </li>
          </ul>
        </nav>
      )}
    </header>
  );
};

export default Header;
